import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { AppState } from 'src/app/app.reducers';
import { SharedService } from 'src/app/Shared/Services/shared.service';
import * as PostsAction from '../../actions';
import { PostDTO } from '../../models/post.dto';
import { PostService } from '../../services/post.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent {
  posts: PostDTO[];
  showButtons: boolean;

  private userId: string;

  constructor(
    private postService: PostService,
    private sharedService: SharedService,
    private store: Store<AppState>
  ) {
    this.userId = '';
    this.posts = new Array<PostDTO>();
    this.showButtons = false;

    this.store.select('auth').subscribe((auth) => {
      this.showButtons = false;

      if (auth.credentials.user_id) {
        this.userId = auth.credentials.user_id;
      }
      if (auth.credentials.access_token) {
        this.showButtons = true;
      }
    });

    this.store.select('posts').subscribe((posts) => {
      this.posts = posts.posts;
    });
  }

  ngOnInit(): void {
    this.loadPosts();
  }

  private loadPosts(): void {
    this.store.dispatch(PostsAction.getPosts());
  }

  like(postId: string): void {
    let errorResponse: any;

    this.postService.likePost(postId).subscribe(
      () => {
        this.loadPosts();
      },
      (error: HttpErrorResponse) => {
        errorResponse = error.error;
        this.sharedService.errorLog(errorResponse);
      }
    );
  }

  dislike(postId: string): void {
    let errorResponse: any;

    this.postService.dislikePost(postId).subscribe(
      () => {
        this.loadPosts();
      },
      (error: HttpErrorResponse) => {
        errorResponse = error.error;
        this.sharedService.errorLog(errorResponse);
      }
    );
  }
}
<p>List All posts</p>

<div style="text-align: center">
  <div *ngFor="let post of posts" class="card">
    <div class="container">
      <div>
        <span
          *ngFor="let category of post.categories"
          class="badge"
          [ngStyle]="{ 'background-color': category.css_color }"
        >
          {{ category.title }}
        </span>
      </div>
    </div>

    <h4>
      <b>Title: {{ post.title }}</b>
    </h4>
    <p>Description: {{ post.description }}</p>
    <p>Likes: {{ post.num_likes }}</p>
    <p>Dislikes: {{ post.num_dislikes }}</p>
    <p>Publication date: {{ post.publication_date | formatDate: 3 }}</p>
    <p>Published by: {{ post.userAlias }}</p>

    <div *ngIf="showButtons">
      <button (click)="like(post.postId)">Like</button>
      <button (click)="dislike(post.postId)">Dislike</button>
    </div>
  </div>
</div>
