import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { Store } from '@ngrx/store';
import { AppState } from 'src/app/app.reducers';
import * as AuthAction from '../actions';
import { AuthDTO } from '../models/auth.dto';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit {
  email: FormControl;
  password: FormControl;
  loginForm: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private store: Store<AppState>
  ) {
    this.email = new FormControl('', [
      Validators.required,
      Validators.pattern('[a-z0-9._%+-]+@[a-z0-9.-]+.[a-z]{2,4}$'),
    ]);

    this.password = new FormControl('', [
      Validators.required,
      Validators.minLength(8),
      Validators.maxLength(16),
    ]);

    this.loginForm = this.formBuilder.group({
      email: this.email,
      password: this.password,
    });
  }

  ngOnInit(): void {}

  login(): void {
    const credentials: AuthDTO = {
      email: this.email.value,
      password: this.password.value,
      user_id: '',
      access_token: '',
    };

    this.store.dispatch(AuthAction.login({ credentials }));
  }
}
<div style="margin-left: auto; margin-right: auto">
  <p id="loginFeedback"></p>
</div>
<div>
  <h1>Login</h1>
  <form [formGroup]="loginForm" (ngSubmit)="login()">
    <div>
      <label for="email">Email: </label>
      <input type="email" [formControl]="email" />
    </div>

    <div>
      <label for="password">Password: </label>
      <input type="password" [formControl]="password" />
    </div>

    <button type="submit" [disabled]="!loginForm.valid">Login</button>
  </form>
</div>

<div *ngIf="email.errors">
  <span
    style="color: red"
    *ngIf="email.errors && (email.touched || email.dirty)"
  >
    <span *ngIf="email.errors.required">Email is required</span>
    <div *ngIf="email.errors.pattern">Email not a valid format.</div>
  </span>
</div>

<div *ngIf="password.errors">
  <span
    style="color: red"
    *ngIf="password.errors && (password.touched || password.dirty)"
  >
    <span *ngIf="password.errors.required">Password is required</span>
    <span *ngIf="password.errors.minlength"
      >Password must be greater tha 8 characters</span
    >
    <span *ngIf="password.errors.maxlength"
      >Password can be max 16 characters long</span
    >
  </span>
</div>
